var simpleDrawer = (function() {
	_checkProperty = function(a) {};
	return {
		toggle: function() {
			var a = $('.drawer-hamburger'),
				c = $('#simple_drawer_header'),
				b = $('#simple_drawer_wrapper');
			if (a && c && b)
				c.click(function() {
					a.hasClass('simple-hamburger-open')
						? (a.removeClass('simple-hamburger-open'),
							a.addClass('simple-hamburger-reverse'),
							b.removeClass('simple-drawer-open'),
							b.addClass('simple-drawer-close'))
						: (b.removeClass('simple-drawer-close'),
							b.addClass('simple-drawer-open'),
							a.removeClass('simple-hamburger-reverse'),
							a.addClass('simple-hamburger-open'));
				});
			else throw Error('not find drawer-hamburger class or simple_drawer_header id');
		}
	};
})();
module.exports = simpleDrawer;
